            for (int x = i; ; ) &amp;#123;
                if (it[x] == g[x].end() || res[x]) &amp;#123;
                    if (!res[x])
                        res[x] = x;
                    res[i] = res[x];
                    for (; !st.empty(); st.pop())
                        res[st.top()] = res[x], inq[st.top()] = 0;
