                &amp;#125;
                st.push(x), inq[x] = 1, x = *it[x];
                if (inq[x])
                    for (int p = -1; p != x; ) &amp;#123;
                        p = st.top(), st.pop();
                        inq[p] = 0, ++it[p];
                    &amp;#125;
            &amp;#125;
        &amp;#125;
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cout &amp;lt;&amp;lt; res[i] &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
