        std::cin &amp;gt;&amp;gt; a[i];
    bld(1, 1, n);
    for (int op, l, r, v; m--; ) &amp;#123;
        std::cin &amp;gt;&amp;gt; op &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r &amp;gt;&amp;gt; v;
        if (op == 1)
            add(1, l, r, v);
        else &amp;#123;
