&lt;hr /&gt;
&lt;h2 id=&#34;a.-关卡设计&#34;&gt;A. 关卡设计&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.becoder.com.cn/contest/6742/problem/1&#34; class=&#34;uri&#34;&gt;https://www.becoder.com.cn/contest/6742/problem/1&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;给定 &lt;span class=&#34;math inline&#34;&gt;\(n\times m\)&lt;/span&gt; 的字符地图，其中 &lt;code&gt;S&lt;/code&gt; 为起点（有且仅有一个），&lt;code&gt;R&lt;/code&gt;，&lt;code&gt;B&lt;/code&gt; 为红、蓝宝石（可能有多个），&lt;code&gt;x&lt;/code&gt; 为障碍、&lt;code&gt;.&lt;/code&gt; 为可通行地块。&lt;/p&gt;
&lt;p&gt;现在需要把尽可能多的 &lt;code&gt;.&lt;/code&gt; 变为 &lt;code&gt;x&lt;/code&gt;，使得一种原来可以由 &lt;code&gt;S&lt;/code&gt; 出发收集到的宝石在修改后的地图中仍能被收集。输出最多可修改的 &lt;code&gt;.&lt;/code&gt; 的数量。&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;math inline&#34;&gt;\(n\times m\le 10^6\)&lt;/span&gt;。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;一种显然错误的做法：拆点 &lt;span class=&#34;math inline&#34;&gt;\((u,0/1/2/3)\)&lt;/span&gt; 分别表示走到 &lt;span class=&#34;math inline&#34;&gt;\(u\)&lt;/span&gt; 时的宝石收集状态，跑 01BFS。&lt;/p&gt;
&lt;p&gt;错误的原因：路径会重复走一些边，但它们不能被重复记入代价。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;结合『重复走一些边』的想法，发现路径一定有一个『分叉点』。枚举这样的分叉点，从 &lt;code&gt;S&lt;/code&gt;、所有 &lt;code&gt;R&lt;/code&gt;、所有 &lt;code&gt;B&lt;/code&gt; 出发分别跑 01BFS。&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;details&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
const int dir[][2] = &amp;#123; &amp;#123; 0, -1 &amp;#125;, &amp;#123; 0, 1 &amp;#125;, &amp;#123; -1, 0 &amp;#125;, &amp;#123; 1, 0 &amp;#125; &amp;#125;;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
    std::freopen(&amp;quot;design.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    std::freopen(&amp;quot;design.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
#else
    std::freopen(&amp;quot;.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    int T;
    for (std::cin &amp;gt;&amp;gt; T; T--; ) &amp;#123;
        int n, m,  cnt = 0;
        std::cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
        std::vector&amp;lt;std::vector&amp;lt;char&amp;gt; &amp;gt; a(n + 1, std::vector&amp;lt;char&amp;gt; (m + 1));
        for (int i = 1; i &amp;lt;= n; ++i)
            for (int j = 1; j &amp;lt;= m; ++j) &amp;#123;
                std::cin &amp;gt;&amp;gt; a[i][j];
                cnt += (a[i][j] == &amp;#39;.&amp;#39;);
            &amp;#125;
        const int N = n * m;
        auto id = [&amp;amp;](int i, int j) &amp;#123; return (i - 1) * m + j; &amp;#125;;
        std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; dis(3, std::vector&amp;lt;int&amp;gt; (N + 1, N + 1));
        auto work = [&amp;amp;](std::vector&amp;lt;int&amp;gt; &amp;amp;dis, char S) &amp;#123;
            std::deque&amp;lt;int&amp;gt; q;
            std::vector&amp;lt;int&amp;gt; vis(N + 1);
            for (int i = 1; i &amp;lt;= n; ++i)
                for (int j = 1; j &amp;lt;= m; ++j)
                    if (a[i][j] == S) &amp;#123;
                        dis[id(i, j)] = 0;
                        q.push_back(id(i, j));
                    &amp;#125;
            for (; !q.empty(); ) &amp;#123;
                int x, y;
                &amp;#123;
                    int t = q.front();
                    q.pop_front();
                    if (vis[t])
                        continue;
                    vis[t] = 1;
                    x = (t - 1) / m + 1, y = t - (x - 1) * m;
                &amp;#125;
                for (auto [fx, fy] : dir) &amp;#123;
                    int nx = x + fx, ny = y + fy;
                    if (nx &amp;gt;= 1 &amp;amp;&amp;amp; ny &amp;gt;= 1 &amp;amp;&amp;amp; nx &amp;lt;= n &amp;amp;&amp;amp; ny &amp;lt;= m &amp;amp;&amp;amp; a[nx][ny] != &amp;#39;x&amp;#39;) &amp;#123;
                        if (a[nx][ny] != &amp;#39;.&amp;#39;) &amp;#123;
                            if (dis[id(nx, ny)] &amp;gt; dis[id(x, y)]) &amp;#123;
                                dis[id(nx, ny)] = dis[id(x, y)];
                                q.push_front(id(nx, ny));
                            &amp;#125;
                        &amp;#125;
                        else if (dis[id(nx, ny)] &amp;gt; dis[id(x, y)] + 1) &amp;#123;
                            dis[id(nx, ny)] = dis[id(x, y)] + 1;
                            q.push_back(id(nx, ny));
                        &amp;#125;
                    &amp;#125;
                &amp;#125;
            &amp;#125;
        &amp;#125;;
        work(dis[0], &amp;#39;S&amp;#39;), work(dis[1], &amp;#39;R&amp;#39;), work(dis[2], &amp;#39;B&amp;#39;);
        int mn1 = N + 1, mn2 = N + 1;
        for (int i = 1; i &amp;lt;= n; ++i)
            for (int j = 1; j &amp;lt;= m; ++j) &amp;#123;
                mn1 = std::min(mn1, dis[0][id(i, j)] + std::min(dis[1][id(i, j)], dis[2][id(i, j)]) - (a[i][j] == &amp;#39;.&amp;#39;));
                mn2 = std::min(mn2, dis[0][id(i, j)] + dis[1][id(i, j)] + dis[2][id(i, j)] - (a[i][j] == &amp;#39;.&amp;#39;) * 2);
            &amp;#125;
        if (mn2 != N + 1)
            std::cout &amp;lt;&amp;lt; cnt - mn2 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
        else if (mn1 != N + 1)
            std::cout &amp;lt;&amp;lt; cnt - mn1 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
        else
            std::cout &amp;lt;&amp;lt; cnt &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
    &amp;#125;;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;/details&gt;
&lt;hr /&gt;
&lt;h2 id=&#34;c.-炼金术&#34;&gt;C. 炼金术&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.becoder.com.cn/contest/6742/problem/3&#34; class=&#34;uri&#34;&gt;https://www.becoder.com.cn/contest/6742/problem/3&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;给定 &lt;span class=&#34;math inline&#34;&gt;\(A_{1\cdots n}\)&lt;/span&gt; 和 &lt;span class=&#34;math inline&#34;&gt;\(B_{1\cdots n}\)&lt;/span&gt;，给定 &lt;span class=&#34;math inline&#34;&gt;\(m\)&lt;/span&gt; 次操作，形如：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;code&gt;1 l r x&lt;/code&gt;，将 &lt;span class=&#34;math inline&#34;&gt;\(A_{l\cdots r}\)&lt;/span&gt; 赋为 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt;。&lt;/li&gt;
&lt;li&gt;&lt;code&gt;2 l r&lt;/code&gt;，询问 &lt;span class=&#34;math inline&#34;&gt;\(\min\limits_{i\in [l, r]}\left\{ \text{lcm}(A_i,B_i)+\dfrac{\text{lcm}(A_i,B_i)}{\gcd(A_i,B_i)} \right\}\)&lt;/span&gt; 的值。&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;span class=&#34;math inline&#34;&gt;\(n,m\le 5\times 10^4,V=5\times 10^4\)&lt;/span&gt;。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;待求没什么太好的性质，考虑更暴力的解决方式。&lt;/p&gt;
&lt;p&gt;容易想到分块做法，对于整块赋值，容易想到对于每个 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 预处理块内答案。如何快速做到这一点？&lt;/p&gt;
&lt;p&gt;一个想法是枚举 gcd 的值 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt;（显然只需枚举 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子），计算 &lt;span class=&#34;math inline&#34;&gt;\(\dfrac {b_j\times x}p+\dfrac {b_j\times x}{p^2}\)&lt;/span&gt; 的最小值。&lt;/p&gt;
&lt;p&gt;&lt;em&gt;Tip：枚举到的 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt; 可能不是 gcd，但能保证一定能找到答案。&lt;/em&gt;&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;贪心地，只需对于块内 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt; 的最小倍数计算答案。这一点对于每个 &lt;span class=&#34;math inline&#34;&gt;\(b_i\)&lt;/span&gt; 枚举因数即能 &lt;span class=&#34;math inline&#34;&gt;\(O(n\sqrt V)\)&lt;/span&gt; 地预处理（因为有重复元素所以不是 &lt;span class=&#34;math inline&#34;&gt;\(O(V\log V)\)&lt;/span&gt;）、花费 &lt;span class=&#34;math inline&#34;&gt;\(O(V\log n)\)&lt;/span&gt; 的空间存储。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;对于每个 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt;，复杂度不允许暴力枚举 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子；发现 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子是 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 与『对于所有 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的质因子 &lt;span class=&#34;math inline&#34;&gt;\(m\)&lt;/span&gt;，&lt;span class=&#34;math inline&#34;&gt;\(\dfrac xm\)&lt;/span&gt; 的因子』的并。&lt;/p&gt;
&lt;p&gt;考虑一个类 DP 的过程，则 &lt;span class=&#34;math inline&#34;&gt;\(f_x\gets f_{x\div m}\times m\)&lt;/span&gt;，再计算 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 自身的答案即可。由于 &lt;span class=&#34;math inline&#34;&gt;\(\omega(V)\)&lt;/span&gt; 平均为 &lt;span class=&#34;math inline&#34;&gt;\(\log\log V\)&lt;/span&gt;，此时预处理复杂度降低至 &lt;span class=&#34;math inline&#34;&gt;\(O(n\sqrt V + V\log V\sqrt n)\)&lt;/span&gt;。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;用一点科技加速 gcd 即可。可选 &lt;span class=&#34;math inline&#34;&gt;\(O(1)\)&lt;/span&gt; gcd，或小范围打表 + 大范围暴力跳至小范围。&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;details&gt;
&lt;p&gt;注意待求可能会爆 &lt;code&gt;unsigned int&lt;/code&gt;，问就是挂分了。&lt;/p&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
    std::freopen(&amp;quot;alchemy.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;alchemy.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
#else
    std::freopen(&amp;quot;./test/20251115/alchemy/alchemy4.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    const long long inf = 1e18;
    const int M = 5000, N = 50000;
    std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; g(M + 1, std::vector&amp;lt;int&amp;gt; (M + 1));
    for (int i = 0; i &amp;lt;= M; ++i) &amp;#123;
        for (int j = 0; j &amp;lt; i; ++j)
            g[i][j] = g[j][i];
        g[i][i] = i;
        for (int j = i + 1; j &amp;lt;= M; ++j)
            g[i][j] = std::__gcd(i, j);
    &amp;#125;
    std::function&amp;lt;int(int, int)&amp;gt; gcd = [&amp;amp;](int x, int y) &amp;#123;
        if (x &amp;lt;= M &amp;amp;&amp;amp; y &amp;lt;= M)
            return g[x][y];
        return y ? gcd(y, x % y) : x;
    &amp;#125;;
    std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; fac(N + 1);
    for (int i = 2; i &amp;lt;= N; ++i)
        if (fac[i].empty())
            for (int j = i; j &amp;lt;= N; j += i)
                fac[j].push_back(i);
    int n, m;
    std::cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
    std::vector&amp;lt;long long&amp;gt; a(n + 1), b(n + 1), p(n + 1);
    auto calc = [&amp;amp;](long long x, long long y) &amp;#123;
        auto g = gcd(x, y);
        return x * y / g + x * y / g / g;
    &amp;#125;;
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; a[i];
    for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
        std::cin &amp;gt;&amp;gt; b[i];
        p[i] = calc(a[i], b[i]);
    &amp;#125;
    int siz = sqrt(n), k = (n + siz - 1) / siz;
    std::vector&amp;lt;long long&amp;gt; u(k + 1, inf);
    std::vector&amp;lt;int&amp;gt; L(k + 1), R(k + 1), id(n + 1), d(k + 1, -1);
    std::vector&amp;lt;std::vector&amp;lt;long long&amp;gt; &amp;gt; mn(k + 1, std::vector&amp;lt;long long&amp;gt; (N + 1, inf)), s(k + 1, std::vector&amp;lt;long long&amp;gt; (N + 1));
    for (int i = 1; i &amp;lt;= k; ++i) &amp;#123;
        L[i] = R[i - 1] + 1, R[i] = std::min(L[i] + siz - 1, n);
        for (int j = L[i]; j &amp;lt;= R[i]; ++j) &amp;#123;
            id[j] = i, u[i] = std::min(u[i], p[j]);
            for (int k = 1; k * k &amp;lt;= b[j]; ++k)
                if (b[j] % k == 0) &amp;#123;
                    mn[i][k] = std::min(mn[i][k], b[j]);
                    mn[i][b[j] / k] = std::min(mn[i][b[j] / k], b[j]);
                &amp;#125;
        &amp;#125;
        s[i][1] = mn[i][1] * 2;
        for (int j = 2; j &amp;lt;= N; ++j) &amp;#123;
            s[i][j] = mn[i][j] + mn[i][j] / j;
            for (auto k : fac[j])
                s[i][j] = std::min(s[i][j], s[i][j / k] * k);
        &amp;#125;
    &amp;#125;
    auto pushdown = [&amp;amp;](int id) &amp;#123;
        if (d[id] != -1) &amp;#123;
            for (int i = L[id]; i &amp;lt;= R[id]; ++i)
                p[i] = calc(d[id], b[i]);
            d[id] = -1;
        &amp;#125;
        return;
    &amp;#125;;
    auto ADD = [&amp;amp;](int l, int r, int x) &amp;#123;
        int pl = id[l], pr = id[r];
        pushdown(pl);
        if (pl == pr) &amp;#123;
            for (int i = l; i &amp;lt;= r; ++i)
                p[i] = calc(x, b[i]);
            u[pl] = *std::min_element(p.begin() + L[pl], p.begin() + R[pl] + 1);
            return;
        &amp;#125;
        pushdown(pr);
        for (int i = l; i &amp;lt;= R[pl]; ++i)
            p[i] = calc(x, b[i]);
        u[pl] = *std::min_element(p.begin() + L[pl], p.begin() + R[pl] + 1);
        for (int i = L[pr]; i &amp;lt;= r; ++i)
            p[i] = calc(x, b[i]);
        u[pr] = *std::min_element(p.begin() + L[pr], p.begin() + R[pr] + 1);
        for (int i = pl + 1; i &amp;lt; pr; ++i)
            d[i] = x, u[i] = s[i][x];
        return;
    &amp;#125;;
    auto ASK = [&amp;amp;](int l, int r) &amp;#123;
        int pl = id[l], pr = id[r];
        pushdown(pl);
        if (pl == pr)
            return *std::min_element(p.begin() + l, p.begin() + r + 1);
        pushdown(pr);
        auto res = std::min(*std::min_element(p.begin() + l, p.begin() + R[pl] + 1), *std::min_element(p.begin() + L[pr], p.begin() + r + 1));
        if (pl + 1 != pr)
            res = std::min(res, *std::min_element(u.begin() + pl + 1, u.begin() + pr));
        return res;
    &amp;#125;;
    for (int op, l, r; m--; ) &amp;#123;
        std::cin &amp;gt;&amp;gt; op &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r;
        if (op == 1) &amp;#123;
            int x;
            std::cin &amp;gt;&amp;gt; x, ADD(l, r, x);
        &amp;#125;
        else
            std::cout &amp;lt;&amp;lt; ASK(l, r) &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
    &amp;#125;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;/details&gt;

 &lt;p&gt;分治树&lt;/p&gt;
&lt;span id=&#34;more&#34;&gt;&lt;/span&gt;
&lt;hr /&gt;
&lt;h2 id=&#34;概念&#34;&gt;概念&lt;/h2&gt;
&lt;ul&gt;
&lt;li&gt;线段树不能维护背包的原因是 pushup 复杂度太高（单次 &lt;span class=&#34;math inline&#34;&gt;\(O(V^2)\)&lt;/span&gt;）。如果确定了要询问的体积（单点 / 区间），能否降低单次合并的复杂度？&lt;/li&gt;
&lt;li&gt;想到 &lt;a href=&#34;/20250520/#贪玩蓝月&#34;&gt;贪玩蓝月&lt;/a&gt; 这个题，如果存在一个 &lt;span class=&#34;math inline&#34;&gt;\(mid\)&lt;/span&gt; 在询问区间内，从 &lt;span class=&#34;math inline&#34;&gt;\(mid\)&lt;/span&gt; 往两侧预处理对应的单侧前后缀答案，最后询问的时候复杂度就比较低&lt;/li&gt;
&lt;li&gt;&lt;p&gt;考虑分治，这样就可以使得对于任意一个询问，总存在一段分治区间将其包含，且分治区间的 &lt;span class=&#34;math inline&#34;&gt;\(mid\)&lt;/span&gt; 在询问内。&lt;/p&gt;
&lt;p&gt;这样就可以单 log 预处理之后，比较快地查询。&lt;/p&gt;
&lt;p&gt;基于这样的预处理形式，所以只能支持静态问题。&lt;/p&gt;
空间复杂度为 &lt;span class=&#34;math inline&#34;&gt;\(O(n\log n)\)&lt;/span&gt;，有一些降空间的实现技巧，这里不赘述&lt;/li&gt;
&lt;li&gt;&lt;p&gt;实现起来会发现很像链上的点分，也会联想到 &lt;a href=&#34;/20250819/#星白-by-ttpandas笛卡尔树-dsu-on-tree&#34;&gt;星白&lt;/a&gt;，只是这个题更像是 dsu on tree。&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;hr /&gt;
&lt;p&gt;对于树上问题，在 uoj 上翻到一个 &lt;span class=&#34;math inline&#34;&gt;\(O(n\log n)\)&lt;/span&gt; 预处理，&lt;span class=&#34;math inline&#34;&gt;\(O(1)\)&lt;/span&gt; 单次查询的做法 &lt;a href=&#34;https://return20071007.blog.uoj.ac/blog/7500&#34;&gt;link&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;实际上并不用猫树，只需要点分树预处理，欧拉序求 LCA 即可。&lt;/p&gt;
&lt;hr /&gt;
&lt;h2 id=&#34;a---好吃的题目&#34;&gt;A - 好吃的题目&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.luogu.com.cn/problem/P6240&#34; class=&#34;uri&#34;&gt;https://www.luogu.com.cn/problem/P6240&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;背包合并的 单点询问 和 前缀询问 都是 &lt;span class=&#34;math inline&#34;&gt;\(O(V)\)&lt;/span&gt; 的，任意区间 是 &lt;span class=&#34;math inline&#34;&gt;\(O(V\log V)\)&lt;/span&gt; 的，也支持取模，像贪玩蓝月那样的&lt;/p&gt;
&lt;p&gt;然后就比较板了&lt;/p&gt;
&lt;p&gt;发现空间不太对，需要把询问离线下来，其实就是经典分治&lt;/p&gt;
&lt;details&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
#else
    std::freopen(&amp;quot;.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    int n, q;
    std::cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; q;
    std::vector&amp;lt;int&amp;gt; v(n + 1), w(n + 1);
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; v[i];
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; w[i];
    n = 1 &amp;lt;&amp;lt; (std::__lg(n) + 1);
    v.resize(n), w.resize(n);
    struct node &amp;#123; int i, l, m; &amp;#125;;
    std::vector&amp;lt;int&amp;gt; res(q + 1);
    std::vector&amp;lt;std::vector&amp;lt;std::vector&amp;lt;node&amp;gt; &amp;gt; &amp;gt; t(17, std::vector&amp;lt;std::vector&amp;lt;node&amp;gt; &amp;gt; (n + 1));
    for (int i = 1, l, r, m; i &amp;lt;= q; ++i) &amp;#123;
        std::cin &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r &amp;gt;&amp;gt; m;
        if (l == r)
            res[i] = (m &amp;gt;= v[l] ? w[l] : 0);
        else &amp;#123;
            int d = std::__lg(l ^ r);
            t[d][r].push_back(&amp;#123; i, l, m &amp;#125;);
        &amp;#125;
    &amp;#125;
    std::vector&amp;lt;std::array&amp;lt;int, 201&amp;gt; &amp;gt; f(n + 1);
    std::function&amp;lt;void(int, int)&amp;gt; calc = [&amp;amp;](int l, int r) &amp;#123;
        int d = std::__lg(r - l + 1) - 1;
        if (l == r)
            return;
        int mid = (l + r) &amp;gt;&amp;gt; 1;
        calc(l, mid), calc(mid + 1, r);
        for (int i = 0; i &amp;lt;= 200; ++i)
            f[mid][i] = (i &amp;gt;= v[mid] ? w[mid] : 0);
        for (int i = mid - 1; i &amp;gt;= l; --i)
            for (int j = 0; j &amp;lt;= 200; ++j) &amp;#123;
                f[i][j] = f[i + 1][j];
                if (v[i] &amp;lt;= j)
                    f[i][j] = std::max(f[i][j], f[i + 1][j - v[i]] + w[i]);
            &amp;#125;
        for (int i = 0; i &amp;lt;= 200; ++i) &amp;#123;
            f[mid + 1][i] = (i &amp;gt;= v[mid + 1] ? w[mid + 1] : 0);
            for (auto [id, l, m] : t[d][mid + 1])
                if (i &amp;lt;= m)
                    res[id] = std::max(res[id], f[mid + 1][i] + f[l][m - i]);
        &amp;#125;
        for (int i = mid + 2; i &amp;lt;= r; ++i)
            for (int j = 0; j &amp;lt;= 200; ++j) &amp;#123;
                f[i][j] = f[i - 1][j];
                if (v[i] &amp;lt;= j)
                    f[i][j] = std::max(f[i][j], f[i - 1][j - v[i]] + w[i]);
                for (auto [id, l, m] : t[d][i])
                    if (j &amp;lt;= m)
                        res[id] = std::max(res[id], f[i][j] + f[l][m - j]);
            &amp;#125;
        return;
    &amp;#125;;
    calc(0, n - 1);
    for (int i = 1; i &amp;lt;= q; ++i)
        std::cout &amp;lt;&amp;lt; res[i] &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;/details&gt;
&lt;hr /&gt;
&lt;h2 id=&#34;b---子序列&#34;&gt;B - 子序列&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.luogu.com.cn/problem/P8885&#34; class=&#34;uri&#34;&gt;https://www.luogu.com.cn/problem/P8885&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;规范称呼：&lt;strong&gt;好子序列&lt;/strong&gt;、&lt;strong&gt;好子串&lt;/strong&gt;、&lt;strong&gt;好整串&lt;/strong&gt;。&lt;/p&gt;
&lt;p&gt;考虑不含 &lt;code&gt;?&lt;/code&gt; 的&lt;strong&gt;好子串&lt;/strong&gt;判定，朴素 DP，令 &lt;span class=&#34;math inline&#34;&gt;\(f_{i,0/1}\)&lt;/span&gt; 表示 DP 到 &lt;span class=&#34;math inline&#34;&gt;\(i\)&lt;/span&gt;，子序列末尾为 &lt;span class=&#34;math inline&#34;&gt;\(0/1\)&lt;/span&gt; 时的子序列数量奇偶性。有：&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;math display&#34;&gt;\[
f_{i,0}=\begin{cases}
f_{i-1,0}+f_{i-1,1}+1&amp;amp;a_i=0\\
f_{i-1,0}&amp;amp;a_i=1
\end{cases},\\f_{i,1}=\begin{cases}
f_{i-1,1}&amp;amp;a_i=0\\
f_{i-1,0}+f_{i-1,1}+1&amp;amp;a_i=1
\end{cases}
\]&lt;/span&gt;&lt;/p&gt;
&lt;p&gt;观察发现 &lt;span class=&#34;math inline&#34;&gt;\(f_{0},f_{1}\)&lt;/span&gt; 中只有至多一个为 &lt;span class=&#34;math inline&#34;&gt;\(1\)&lt;/span&gt;。令 &lt;span class=&#34;math inline&#34;&gt;\(f_2\)&lt;/span&gt; 为一当且仅当 &lt;span class=&#34;math inline&#34;&gt;\(f_0,f_1\)&lt;/span&gt; 均不为 &lt;span class=&#34;math inline&#34;&gt;\(1\)&lt;/span&gt;。考虑 &lt;span class=&#34;math inline&#34;&gt;\(1\)&lt;/span&gt; 所在位置，建立 DFA：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;image.png&#34; /&gt;&lt;/p&gt;
&lt;p&gt;如需加入 &lt;code&gt;?&lt;/code&gt;，转化为 NFA。注意到只关心每个点上指针数量的奇偶性，故压缩为 &lt;span class=&#34;math inline&#34;&gt;\({c_0,c_1,c_2}\)&lt;/span&gt; 记录。考虑对&lt;strong&gt;好子串&lt;/strong&gt;计数。每次在 &lt;span class=&#34;math inline&#34;&gt;\(2\)&lt;/span&gt; 处加入一个新的 &lt;span class=&#34;math inline&#34;&gt;\(1\)&lt;/span&gt; 表示空串，再令所有指针移动一步，并对当前点结尾的子串数量计数，也即 &lt;span class=&#34;math inline&#34;&gt;\(f_{a_i}\)&lt;/span&gt;。即完成对好子串的计数。&lt;/p&gt;
&lt;p&gt;考虑进一步 DP of DP，将计数变量 &lt;span class=&#34;math inline&#34;&gt;\(tot\)&lt;/span&gt; 的奇偶性放入状态。初始为 &lt;span class=&#34;math inline&#34;&gt;\(0,0,0,0\)&lt;/span&gt;，转移 &lt;span class=&#34;math inline&#34;&gt;\(dp_{f0,f1,f2,tot}\to dp_{f2\oplus1,f1,f0,tot\oplus f0}\)&lt;/span&gt;，最后在 &lt;span class=&#34;math inline&#34;&gt;\(*,*,*,1\)&lt;/span&gt; 查答案。状态数共为 &lt;span class=&#34;math inline&#34;&gt;\(2^4=16\)&lt;/span&gt;，使用 DDP 维护动态询问即可。由于矩阵略大，需要用猫树替换线段树。然后就可以得到一个会 TLE 80 的做法（注意 &lt;code&gt;?&lt;/code&gt; 的转移矩阵系数可能为 2）。&lt;/p&gt;
&lt;details&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
const int mod = 998244353;
struct mat &amp;#123;
    long long a[16][16];
    long long* operator[] (const int q) &amp;#123;
        return a[q];
    &amp;#125;
    mat() &amp;#123;
        return;
    &amp;#125;
    mat operator* (mat &amp;amp;q) const &amp;#123;
        mat res;
        for (int i = 0; i &amp;lt; 16; ++i)
            for (int k = 0; k &amp;lt; 16; ++k) &amp;#123;
                auto s = 0ll;
                for (int j = 0; j &amp;lt; 16; ++j)
                    s += a[i][j] * q[j][k];
                res[i][k] = s % mod;
            &amp;#125;
        return res;
    &amp;#125;
    mat&amp;amp; operator*= (mat &amp;amp;q) &amp;#123;
        return *this = *this * q;
    &amp;#125;
&amp;#125; m0, m1, ma;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
#else
    std::freopen(&amp;quot;.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    for (int f0 = 0; f0 &amp;lt;= 1; ++f0)
        for (int f1 = 0; f1 &amp;lt;= 1; ++f1)
            for (int f2 = 0; f2 &amp;lt;= 1; ++f2)
                for (int tot = 0; tot &amp;lt;= 1; ++tot) &amp;#123;
                    m0[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot][((f2 ^ 1) &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f0 &amp;lt;&amp;lt; 1) + (tot ^ f0)] = 1ll;
                    ma[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot][((f2 ^ 1) &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f0 &amp;lt;&amp;lt; 1) + (tot ^ f0)] = 1LL;
                    m1[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot][(f0 &amp;lt;&amp;lt; 3) + ((f2 ^ 1) &amp;lt;&amp;lt; 2) + (f1 &amp;lt;&amp;lt; 1) + (tot ^ f1)] = 1ll;
                    ++ma[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot][(f0 &amp;lt;&amp;lt; 3) + ((f2 ^ 1) &amp;lt;&amp;lt; 2) + (f1 &amp;lt;&amp;lt; 1) + (tot ^ f1)];
                &amp;#125;
    int n, n1;
    std::cin &amp;gt;&amp;gt; n;
    n1 = 1 &amp;lt;&amp;lt; (std::__lg(n + 1) + 1);
    std::vector&amp;lt;char&amp;gt; a(n + 1);
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; a[i];
    std::vector&amp;lt;mat&amp;gt; f(std::vector&amp;lt;mat&amp;gt; (n + 1));
    auto askmat = [&amp;amp;](char t) -&amp;gt; mat&amp;amp; &amp;#123;
        if (t == &amp;#39;0&amp;#39;)
            return m0;
        if (t == &amp;#39;1&amp;#39;)
            return m1;
        return ma;
    &amp;#125;;
    int m;
    std::cin &amp;gt;&amp;gt; m;
    std::vector&amp;lt;long long&amp;gt; res(m + 1);
    std::vector&amp;lt;std::vector&amp;lt;std::vector&amp;lt;std::pair&amp;lt;int, int&amp;gt; &amp;gt; &amp;gt; &amp;gt; u(std::__lg(n1), std::vector&amp;lt;std::vector&amp;lt; std::pair&amp;lt;int, int&amp;gt; &amp;gt; &amp;gt; (n + 1));
    for (int i = 1, l, r; i &amp;lt;= m; ++i) &amp;#123;
        std::cin &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r;
        mat k;
        if (l == r) &amp;#123;
            k = askmat(a[l]);
            auto sum(0ll);
            for (int s = 0; s &amp;lt; 8; ++s)
                sum += k[0][(s &amp;lt;&amp;lt; 1) + 1];
            res[i] = sum % mod;
        &amp;#125;
        else &amp;#123;
            int d = std::__lg(l ^ r);
            u[d][r].emplace_back(l, i);
        &amp;#125;
    &amp;#125;
    std::function&amp;lt;void(int, int)&amp;gt; calc = [&amp;amp;](int l, int r) &amp;#123;
        if (l == r)
            return;
        int d = std::__lg(r - l + 1) - 1, mid = (l + r) &amp;gt;&amp;gt; 1;
        calc(l, mid), calc(mid + 1, r);
        if (mid &amp;lt;= n &amp;amp;&amp;amp; mid) &amp;#123;
            f[mid] = askmat(a[mid]);
            for (int i = mid - 1; i &amp;gt;= l &amp;amp;&amp;amp; i; --i)
                f[i] = askmat(a[i]) * f[i + 1];
        &amp;#125;
        if (mid + 1 &amp;lt;= n) &amp;#123;
            f[mid + 1] = askmat(a[mid + 1]);
            for (auto [l, id] : u[d][mid + 1]) &amp;#123;
                auto sum(0ll);
                auto k(f[l] * f[mid + 1]);
                for (int s = 0; s &amp;lt; 8; ++s)
                    sum += k[0][(s &amp;lt;&amp;lt; 1) + 1];
                res[id] = sum % mod;
            &amp;#125;
            for (int i = mid + 2; i &amp;lt;= r &amp;amp;&amp;amp; i &amp;lt;= n; ++i) &amp;#123;
                f[i] = f[i - 1] * askmat(a[i]);
                for (auto [l, id] : u[d][i]) &amp;#123;
                    auto sum(0ll);
                    auto k(f[l] * f[i]);
                    for (int s = 0; s &amp;lt; 8; ++s)
                        sum += k[0][(s &amp;lt;&amp;lt; 1) + 1];
                    res[id] = sum % mod;
                &amp;#125;
            &amp;#125;
        &amp;#125;
        return;
    &amp;#125;;
    calc(0, n1 - 1);
    for (int i = 1; i &amp;lt;= m; ++i)
        std::cout &amp;lt;&amp;lt; res[i] &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;/details&gt;
&lt;p&gt;观察 &lt;code&gt;?&lt;/code&gt; 的转移矩阵，发现其实很稀疏，有效边只有 28 条&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;image-1.png&#34; /&gt;&lt;/p&gt;
&lt;p&gt;那么完全可以抛弃矩阵这个概念，返璞归真用线性 DP 解决；枚举中间相接的位就可以合并。&lt;/p&gt;
&lt;p&gt;实际上另一种更数学的理解方式是在左侧乘一个 &lt;span class=&#34;math inline&#34;&gt;\([1,0,0,\cdots,0]\)&lt;/span&gt; 就可以把左边的矩阵转化成向量；通过枚举中间相接的位，相当于在 &lt;span class=&#34;math inline&#34;&gt;\(mid\)&lt;/span&gt; 处乘一个 &lt;span class=&#34;math inline&#34;&gt;\([0,0,\cdots,0,1,0,\cdots,0]\)&lt;/span&gt; 的向量，右边的矩阵就也转化成了向量。接着，由于矩阵很稀疏，只有 28 个位置有值，故可以手动转移。&lt;/p&gt;
&lt;p&gt;这样就可以少一个 &lt;span class=&#34;math inline&#34;&gt;\(O(s)\)&lt;/span&gt;，可以通过。&lt;/p&gt;
&lt;details&gt;
&lt;p&gt;贺了 &lt;span class=&#34;citation&#34; data-cites=&#34;spdarkle&#34;&gt;@spdarkle&lt;/span&gt; 的代码，但跑得没他快&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;citation&#34; data-cites=&#34;Rosmist&#34;&gt;@Rosmist&lt;/span&gt; 又在表演卡常技能了，勇夺最优解&lt;/p&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
const int mod = 998244353;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
#else
    std::freopen(&amp;quot;.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    std::vector&amp;lt;int&amp;gt; m0(16), m1(16);
    for (int f0 = 0; f0 &amp;lt;= 1; ++f0)
        for (int f1 = 0; f1 &amp;lt;= 1; ++f1)
            for (int f2 = 0; f2 &amp;lt;= 1; ++f2)
                for (int tot = 0; tot &amp;lt;= 1; ++tot) &amp;#123;
                    m0[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot] = ((f2 ^ 1) &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f0 &amp;lt;&amp;lt; 1) + (tot ^ f0);
                    m1[(f0 &amp;lt;&amp;lt; 3) + (f1 &amp;lt;&amp;lt; 2) + (f2 &amp;lt;&amp;lt; 1) + tot] = (f0 &amp;lt;&amp;lt; 3) + ((f2 ^ 1) &amp;lt;&amp;lt; 2) + (f1 &amp;lt;&amp;lt; 1) + (tot ^ f1);
                &amp;#125;
    int n, n1;
    std::cin &amp;gt;&amp;gt; n;
    n1 = 1 &amp;lt;&amp;lt; (std::__lg(n + 1) + 1);
    std::vector&amp;lt;char&amp;gt; a(n + 1);
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; a[i];
    std::vector&amp;lt;int&amp;gt; dp(n + 1);
    std::vector&amp;lt;std::array&amp;lt;int, 16&amp;gt; &amp;gt; f(n + 1);
    int m;
    std::cin &amp;gt;&amp;gt; m;
    std::vector&amp;lt;long long&amp;gt; res(m + 1);
    std::vector&amp;lt;std::vector&amp;lt;std::vector&amp;lt;std::pair&amp;lt;int, int&amp;gt; &amp;gt; &amp;gt; &amp;gt; u(std::__lg(n1), std::vector&amp;lt;std::vector&amp;lt; std::pair&amp;lt;int, int&amp;gt; &amp;gt; &amp;gt; (n + 1));
    for (int i = 1, l, r; i &amp;lt;= m; ++i) &amp;#123;
        std::cin &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r;
        if (l != r) &amp;#123;
            int d = std::__lg(l ^ r);
            u[d][r].emplace_back(l, i);
        &amp;#125;
    &amp;#125;
    std::function&amp;lt;void(int, int)&amp;gt; calc = [&amp;amp;](int l, int r) &amp;#123;
        if (l == r)
            return;
        int d = std::__lg(r - l + 1) - 1, mid = (l + r) &amp;gt;&amp;gt; 1;
        calc(l, mid), calc(mid + 1, r);
        for (int t = 0; t &amp;lt; 16; ++t) &amp;#123;
            std::array&amp;lt;int, 16&amp;gt; la;
            if (mid &amp;lt;= n &amp;amp;&amp;amp; mid) &amp;#123;
                std::fill(la.begin(), la.end(), 0);
                la[t] = 1ll;
                for (int i = mid; i &amp;gt;= l &amp;amp;&amp;amp; i; --i) &amp;#123;
                    std::fill(f[i].begin(), f[i].end(), 0);
                    if (a[i] != &amp;#39;1&amp;#39;)
                        for (int j = 0; j &amp;lt; 16; ++j)
                            f[i][j] = la[m0[j]];
                    if (a[i] != &amp;#39;0&amp;#39;)
                        for (int j = 0; j &amp;lt; 16; ++j) &amp;#123;
                            f[i][j] += la[m1[j]];
                            if (f[i][j] &amp;gt;= mod)
                                f[i][j] -= mod;
                        &amp;#125;
                    dp[i] = f[i][0];
                    la = f[i];
                &amp;#125;
            &amp;#125;
            if (mid + 1 &amp;lt;= n) &amp;#123;
                std::fill(la.begin(), la.end(), 0);
                la[t] = 1ll;
                for (int i = mid + 1; i &amp;lt;= r &amp;amp;&amp;amp; i &amp;lt;= n; ++i) &amp;#123;
                    std::fill(f[i].begin(), f[i].end(), 0);
                    if (a[i] != &amp;#39;1&amp;#39;)
                        for (int j = 0; j &amp;lt; 16; ++j)
                            f[i][m0[j]] = la[j];
                    if (a[i] != &amp;#39;0&amp;#39;)
                        for (int j = 0; j &amp;lt; 16; ++j) &amp;#123;
                            f[i][m1[j]] += la[j];
                            if (f[i][m1[j]] &amp;gt;= mod)
                                f[i][m1[j]] -= mod;
                        &amp;#125;
                    dp[i] = 0ll;
                    for (int s = 0; s &amp;lt; 8; ++s) &amp;#123;
                        dp[i] += f[i][(s &amp;lt;&amp;lt; 1) + 1];
                        if (dp[i] &amp;gt;= mod)
                            dp[i] -= mod;
                    &amp;#125;
                    for (auto [l, id] : u[d][i])
                        (res[id] += (long long)dp[l] * dp[i]) %= mod;
                    la = f[i];
                &amp;#125;
            &amp;#125;
        &amp;#125;
        return;
    &amp;#125;;
    calc(0, n1 - 1);
    for (int i = 1; i &amp;lt;= m; ++i)
        std::cout &amp;lt;&amp;lt; res[i] &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
