        for (;;) &amp;#123;
            p[i].push_back(y); 
            if (a[x + 1][y] == &amp;#39;.&amp;#39;) ++x;
            else &amp;#123;
                if (a[x + 1][y] == &amp;#39;X&amp;#39;) break;
                if (a[x][y - 1] == &amp;#39;.&amp;#39; &amp;amp;&amp;amp; a[x + 1][y - 1] == &amp;#39;.&amp;#39;)
