                        if (sx[x] != 0)
                            return false;
                        fx[x] = 1;
                        if (!check())
                            return false;
                    &amp;#125;
                    else if (tx[x] == n - 1) &amp;#123;
                        for (int y = 1; y &amp;lt;= n; ++y)
                            if (!vis[x][y]) &amp;#123;
                                work(x, y, sx[x]);
                                fx[x] = 1;
                                if (!check())
                                    return false;
                            &amp;#125;
                    &amp;#125;
                &amp;#125;
            for (int y = 1; y &amp;lt;= n; ++y)
                if (!fy[y]) &amp;#123;
                    if (ty[y] == n) &amp;#123;
                        if (sy[y] != 0)
                            return false;
                        fy[y] = 1;
                        if (!check())
                            return false;
                    &amp;#125;
                    else if (ty[y] == n - 1) &amp;#123;
                        for (int x = 1; x &amp;lt;= n; ++x)
                            if (!vis[x][y]) &amp;#123;
                                work(x, y, sy[y]);
                                fy[y] = 1;
                                if (!check())
                                    return false;
