        else
            std::cout &amp;lt;&amp;lt; res[N - 1].f &amp;lt;&amp;lt; &amp;#39; &amp;#39; &amp;lt;&amp;lt; res[N - 1].g &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
    &amp;#125;
