&lt;h2 id=&#34;a.-关卡设计&#34;&gt;A. 关卡设计&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.becoder.com.cn/contest/6742/problem/1&#34; class=&#34;uri&#34;&gt;https://www.becoder.com.cn/contest/6742/problem/1&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;给定 &lt;span class=&#34;math inline&#34;&gt;\(n\times m\)&lt;/span&gt; 的字符地图，其中 &lt;code&gt;S&lt;/code&gt; 为起点（有且仅有一个），&lt;code&gt;R&lt;/code&gt;，&lt;code&gt;B&lt;/code&gt; 为红、蓝宝石（可能有多个），&lt;code&gt;x&lt;/code&gt; 为障碍、&lt;code&gt;.&lt;/code&gt; 为可通行地块。&lt;/p&gt;
&lt;p&gt;现在需要把尽可能多的 &lt;code&gt;.&lt;/code&gt; 变为 &lt;code&gt;x&lt;/code&gt;，使得一种原来可以由 &lt;code&gt;S&lt;/code&gt; 出发收集到的宝石在修改后的地图中仍能被收集。输出最多可修改的 &lt;code&gt;.&lt;/code&gt; 的数量。&lt;/p&gt;
&lt;p&gt;&lt;span class=&#34;math inline&#34;&gt;\(n\times m\le 10^6\)&lt;/span&gt;。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;一种显然错误的做法：拆点 &lt;span class=&#34;math inline&#34;&gt;\((u,0/1/2/3)\)&lt;/span&gt; 分别表示走到 &lt;span class=&#34;math inline&#34;&gt;\(u\)&lt;/span&gt; 时的宝石收集状态，跑 01BFS。&lt;/p&gt;
&lt;p&gt;错误的原因：路径会重复走一些边，但它们不能被重复记入代价。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;结合『重复走一些边』的想法，发现路径一定有一个『分叉点』。枚举这样的分叉点，从 &lt;code&gt;S&lt;/code&gt;、所有 &lt;code&gt;R&lt;/code&gt;、所有 &lt;code&gt;B&lt;/code&gt; 出发分别跑 01BFS。&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;details&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
const int dir[][2] = &amp;#123; &amp;#123; 0, -1 &amp;#125;, &amp;#123; 0, 1 &amp;#125;, &amp;#123; -1, 0 &amp;#125;, &amp;#123; 1, 0 &amp;#125; &amp;#125;;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
    std::freopen(&amp;quot;design.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    std::freopen(&amp;quot;design.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
#else
    std::freopen(&amp;quot;.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    int T;
    for (std::cin &amp;gt;&amp;gt; T; T--; ) &amp;#123;
        int n, m,  cnt = 0;
        std::cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
        std::vector&amp;lt;std::vector&amp;lt;char&amp;gt; &amp;gt; a(n + 1, std::vector&amp;lt;char&amp;gt; (m + 1));
        for (int i = 1; i &amp;lt;= n; ++i)
            for (int j = 1; j &amp;lt;= m; ++j) &amp;#123;
                std::cin &amp;gt;&amp;gt; a[i][j];
                cnt += (a[i][j] == &amp;#39;.&amp;#39;);
            &amp;#125;
        const int N = n * m;
        auto id = [&amp;amp;](int i, int j) &amp;#123; return (i - 1) * m + j; &amp;#125;;
        std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; dis(3, std::vector&amp;lt;int&amp;gt; (N + 1, N + 1));
        auto work = [&amp;amp;](std::vector&amp;lt;int&amp;gt; &amp;amp;dis, char S) &amp;#123;
            std::deque&amp;lt;int&amp;gt; q;
            std::vector&amp;lt;int&amp;gt; vis(N + 1);
            for (int i = 1; i &amp;lt;= n; ++i)
                for (int j = 1; j &amp;lt;= m; ++j)
                    if (a[i][j] == S) &amp;#123;
                        dis[id(i, j)] = 0;
                        q.push_back(id(i, j));
                    &amp;#125;
            for (; !q.empty(); ) &amp;#123;
                int x, y;
                &amp;#123;
                    int t = q.front();
                    q.pop_front();
                    if (vis[t])
                        continue;
                    vis[t] = 1;
                    x = (t - 1) / m + 1, y = t - (x - 1) * m;
                &amp;#125;
                for (auto [fx, fy] : dir) &amp;#123;
                    int nx = x + fx, ny = y + fy;
                    if (nx &amp;gt;= 1 &amp;amp;&amp;amp; ny &amp;gt;= 1 &amp;amp;&amp;amp; nx &amp;lt;= n &amp;amp;&amp;amp; ny &amp;lt;= m &amp;amp;&amp;amp; a[nx][ny] != &amp;#39;x&amp;#39;) &amp;#123;
                        if (a[nx][ny] != &amp;#39;.&amp;#39;) &amp;#123;
                            if (dis[id(nx, ny)] &amp;gt; dis[id(x, y)]) &amp;#123;
                                dis[id(nx, ny)] = dis[id(x, y)];
                                q.push_front(id(nx, ny));
                            &amp;#125;
                        &amp;#125;
                        else if (dis[id(nx, ny)] &amp;gt; dis[id(x, y)] + 1) &amp;#123;
                            dis[id(nx, ny)] = dis[id(x, y)] + 1;
                            q.push_back(id(nx, ny));
                        &amp;#125;
                    &amp;#125;
                &amp;#125;
            &amp;#125;
        &amp;#125;;
        work(dis[0], &amp;#39;S&amp;#39;), work(dis[1], &amp;#39;R&amp;#39;), work(dis[2], &amp;#39;B&amp;#39;);
        int mn1 = N + 1, mn2 = N + 1;
        for (int i = 1; i &amp;lt;= n; ++i)
            for (int j = 1; j &amp;lt;= m; ++j) &amp;#123;
                mn1 = std::min(mn1, dis[0][id(i, j)] + std::min(dis[1][id(i, j)], dis[2][id(i, j)]) - (a[i][j] == &amp;#39;.&amp;#39;));
                mn2 = std::min(mn2, dis[0][id(i, j)] + dis[1][id(i, j)] + dis[2][id(i, j)] - (a[i][j] == &amp;#39;.&amp;#39;) * 2);
            &amp;#125;
        if (mn2 != N + 1)
            std::cout &amp;lt;&amp;lt; cnt - mn2 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
        else if (mn1 != N + 1)
            std::cout &amp;lt;&amp;lt; cnt - mn1 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
        else
            std::cout &amp;lt;&amp;lt; cnt &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
    &amp;#125;;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
&lt;/details&gt;
&lt;hr /&gt;
&lt;h2 id=&#34;c.-炼金术&#34;&gt;C. 炼金术&lt;/h2&gt;
&lt;p&gt;&lt;a href=&#34;https://www.becoder.com.cn/contest/6742/problem/3&#34; class=&#34;uri&#34;&gt;https://www.becoder.com.cn/contest/6742/problem/3&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;给定 &lt;span class=&#34;math inline&#34;&gt;\(A_{1\cdots n}\)&lt;/span&gt; 和 &lt;span class=&#34;math inline&#34;&gt;\(B_{1\cdots n}\)&lt;/span&gt;，给定 &lt;span class=&#34;math inline&#34;&gt;\(m\)&lt;/span&gt; 次操作，形如：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;&lt;code&gt;1 l r x&lt;/code&gt;，将 &lt;span class=&#34;math inline&#34;&gt;\(A_{l\cdots r}\)&lt;/span&gt; 赋为 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt;。&lt;/li&gt;
&lt;li&gt;&lt;code&gt;2 l r&lt;/code&gt;，询问 &lt;span class=&#34;math inline&#34;&gt;\(\min\limits_{i\in [l, r]}\left\{ \text{lcm}(A_i,B_i)+\dfrac{\text{lcm}(A_i,B_i)}{\gcd(A_i,B_i)} \right\}\)&lt;/span&gt; 的值。&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;span class=&#34;math inline&#34;&gt;\(n,m\le 5\times 10^4,V=5\times 10^4\)&lt;/span&gt;。&lt;/p&gt;
&lt;/blockquote&gt;
&lt;ul&gt;
&lt;li&gt;&lt;p&gt;待求没什么太好的性质，考虑更暴力的解决方式。&lt;/p&gt;
&lt;p&gt;容易想到分块做法，对于整块赋值，容易想到对于每个 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 预处理块内答案。如何快速做到这一点？&lt;/p&gt;
&lt;p&gt;一个想法是枚举 gcd 的值 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt;（显然只需枚举 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子），计算 &lt;span class=&#34;math inline&#34;&gt;\(\dfrac {b_j\times x}p+\dfrac {b_j\times x}{p^2}\)&lt;/span&gt; 的最小值。&lt;/p&gt;
&lt;p&gt;&lt;em&gt;Tip：枚举到的 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt; 可能不是 gcd，但能保证一定能找到答案。&lt;/em&gt;&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;贪心地，只需对于块内 &lt;span class=&#34;math inline&#34;&gt;\(p\)&lt;/span&gt; 的最小倍数计算答案。这一点对于每个 &lt;span class=&#34;math inline&#34;&gt;\(b_i\)&lt;/span&gt; 枚举因数即能 &lt;span class=&#34;math inline&#34;&gt;\(O(n\sqrt V)\)&lt;/span&gt; 地预处理（因为有重复元素所以不是 &lt;span class=&#34;math inline&#34;&gt;\(O(V\log V)\)&lt;/span&gt;）、花费 &lt;span class=&#34;math inline&#34;&gt;\(O(V\log n)\)&lt;/span&gt; 的空间存储。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;对于每个 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt;，复杂度不允许暴力枚举 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子；发现 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的因子是 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 与『对于所有 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 的质因子 &lt;span class=&#34;math inline&#34;&gt;\(m\)&lt;/span&gt;，&lt;span class=&#34;math inline&#34;&gt;\(\dfrac xm\)&lt;/span&gt; 的因子』的并。&lt;/p&gt;
&lt;p&gt;考虑一个类 DP 的过程，则 &lt;span class=&#34;math inline&#34;&gt;\(f_x\gets f_{x\div m}\times m\)&lt;/span&gt;，再计算 &lt;span class=&#34;math inline&#34;&gt;\(x\)&lt;/span&gt; 自身的答案即可。由于 &lt;span class=&#34;math inline&#34;&gt;\(\omega(V)\)&lt;/span&gt; 平均为 &lt;span class=&#34;math inline&#34;&gt;\(\log\log V\)&lt;/span&gt;，此时预处理复杂度降低至 &lt;span class=&#34;math inline&#34;&gt;\(O(n\sqrt V + V\log V\sqrt n)\)&lt;/span&gt;。&lt;/p&gt;&lt;/li&gt;
&lt;li&gt;&lt;p&gt;用一点科技加速 gcd 即可。可选 &lt;span class=&#34;math inline&#34;&gt;\(O(1)\)&lt;/span&gt; gcd，或小范围打表 + 大范围暴力跳至小范围。&lt;/p&gt;&lt;/li&gt;
&lt;/ul&gt;
&lt;details&gt;
&lt;p&gt;注意待求可能会爆 &lt;code&gt;unsigned int&lt;/code&gt;，问就是挂分了。&lt;/p&gt;
&lt;pre class=&#34;cpp&#34;&gt;&lt;code&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
int main() &amp;#123;
#ifdef ONLINE_JUDGE
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr), std::cout.tie(nullptr);
    std::freopen(&amp;quot;alchemy.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;alchemy.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
#else
    std::freopen(&amp;quot;./test/20251115/alchemy/alchemy4.in&amp;quot;, &amp;quot;r&amp;quot;, stdin);
    std::freopen(&amp;quot;.out&amp;quot;, &amp;quot;w&amp;quot;, stdout);
    const auto stime = std::chrono::steady_clock::now();
#endif
    const long long inf = 1e18;
    const int M = 5000, N = 50000;
    std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; g(M + 1, std::vector&amp;lt;int&amp;gt; (M + 1));
    for (int i = 0; i &amp;lt;= M; ++i) &amp;#123;
        for (int j = 0; j &amp;lt; i; ++j)
            g[i][j] = g[j][i];
        g[i][i] = i;
        for (int j = i + 1; j &amp;lt;= M; ++j)
            g[i][j] = std::__gcd(i, j);
    &amp;#125;
    std::function&amp;lt;int(int, int)&amp;gt; gcd = [&amp;amp;](int x, int y) &amp;#123;
        if (x &amp;lt;= M &amp;amp;&amp;amp; y &amp;lt;= M)
            return g[x][y];
        return y ? gcd(y, x % y) : x;
    &amp;#125;;
    std::vector&amp;lt;std::vector&amp;lt;int&amp;gt; &amp;gt; fac(N + 1);
    for (int i = 2; i &amp;lt;= N; ++i)
        if (fac[i].empty())
            for (int j = i; j &amp;lt;= N; j += i)
                fac[j].push_back(i);
    int n, m;
    std::cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m;
    std::vector&amp;lt;long long&amp;gt; a(n + 1), b(n + 1), p(n + 1);
    auto calc = [&amp;amp;](long long x, long long y) &amp;#123;
        auto g = gcd(x, y);
        return x * y / g + x * y / g / g;
    &amp;#125;;
    for (int i = 1; i &amp;lt;= n; ++i)
        std::cin &amp;gt;&amp;gt; a[i];
    for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
        std::cin &amp;gt;&amp;gt; b[i];
        p[i] = calc(a[i], b[i]);
    &amp;#125;
    int siz = sqrt(n), k = (n + siz - 1) / siz;
    std::vector&amp;lt;long long&amp;gt; u(k + 1, inf);
    std::vector&amp;lt;int&amp;gt; L(k + 1), R(k + 1), id(n + 1), d(k + 1, -1);
    std::vector&amp;lt;std::vector&amp;lt;long long&amp;gt; &amp;gt; mn(k + 1, std::vector&amp;lt;long long&amp;gt; (N + 1, inf)), s(k + 1, std::vector&amp;lt;long long&amp;gt; (N + 1));
    for (int i = 1; i &amp;lt;= k; ++i) &amp;#123;
        L[i] = R[i - 1] + 1, R[i] = std::min(L[i] + siz - 1, n);
        for (int j = L[i]; j &amp;lt;= R[i]; ++j) &amp;#123;
            id[j] = i, u[i] = std::min(u[i], p[j]);
            for (int k = 1; k * k &amp;lt;= b[j]; ++k)
                if (b[j] % k == 0) &amp;#123;
                    mn[i][k] = std::min(mn[i][k], b[j]);
                    mn[i][b[j] / k] = std::min(mn[i][b[j] / k], b[j]);
                &amp;#125;
        &amp;#125;
        s[i][1] = mn[i][1] * 2;
        for (int j = 2; j &amp;lt;= N; ++j) &amp;#123;
            s[i][j] = mn[i][j] + mn[i][j] / j;
            for (auto k : fac[j])
                s[i][j] = std::min(s[i][j], s[i][j / k] * k);
        &amp;#125;
    &amp;#125;
    auto pushdown = [&amp;amp;](int id) &amp;#123;
        if (d[id] != -1) &amp;#123;
            for (int i = L[id]; i &amp;lt;= R[id]; ++i)
                p[i] = calc(d[id], b[i]);
            d[id] = -1;
        &amp;#125;
        return;
    &amp;#125;;
    auto ADD = [&amp;amp;](int l, int r, int x) &amp;#123;
        int pl = id[l], pr = id[r];
        pushdown(pl);
        if (pl == pr) &amp;#123;
            for (int i = l; i &amp;lt;= r; ++i)
                p[i] = calc(x, b[i]);
            u[pl] = *std::min_element(p.begin() + L[pl], p.begin() + R[pl] + 1);
            return;
        &amp;#125;
        pushdown(pr);
        for (int i = l; i &amp;lt;= R[pl]; ++i)
            p[i] = calc(x, b[i]);
        u[pl] = *std::min_element(p.begin() + L[pl], p.begin() + R[pl] + 1);
        for (int i = L[pr]; i &amp;lt;= r; ++i)
            p[i] = calc(x, b[i]);
        u[pr] = *std::min_element(p.begin() + L[pr], p.begin() + R[pr] + 1);
        for (int i = pl + 1; i &amp;lt; pr; ++i)
            d[i] = x, u[i] = s[i][x];
        return;
    &amp;#125;;
    auto ASK = [&amp;amp;](int l, int r) &amp;#123;
        int pl = id[l], pr = id[r];
        pushdown(pl);
        if (pl == pr)
            return *std::min_element(p.begin() + l, p.begin() + r + 1);
        pushdown(pr);
        auto res = std::min(*std::min_element(p.begin() + l, p.begin() + R[pl] + 1), *std::min_element(p.begin() + L[pr], p.begin() + r + 1));
        if (pl + 1 != pr)
            res = std::min(res, *std::min_element(u.begin() + pl + 1, u.begin() + pr));
        return res;
    &amp;#125;;
    for (int op, l, r; m--; ) &amp;#123;
        std::cin &amp;gt;&amp;gt; op &amp;gt;&amp;gt; l &amp;gt;&amp;gt; r;
        if (op == 1) &amp;#123;
            int x;
            std::cin &amp;gt;&amp;gt; x, ADD(l, r, x);
        &amp;#125;
        else
            std::cout &amp;lt;&amp;lt; ASK(l, r) &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
    &amp;#125;
#ifndef ONLINE_JUDGE
    std::cerr &amp;lt;&amp;lt; std::fixed &amp;lt;&amp;lt; std::setprecision(6) &amp;lt;&amp;lt; std::chrono::duration&amp;lt;double&amp;gt; (std::chrono::steady_clock::now() - stime).count() &amp;lt;&amp;lt; &amp;quot;s\n&amp;quot;;
#endif
    return 0;
&amp;#125;&lt;/code&gt;&lt;/pre&gt;
