                break;
            &amp;#125;
        if (pos != -1) &amp;#123;
            std::cout &amp;lt;&amp;lt; 2 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
            for (int i = 1; i &amp;lt;= m; ++i)
                if (e[i].first == pos || e[i].second == pos)
                    std::cout &amp;lt;&amp;lt; 1 &amp;lt;&amp;lt; &amp;#39; &amp;#39;;
