        res = 0;
        for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
            for (int j = 1; j &amp;lt;= n; ++j)
                read(g[i][j + n]);
        &amp;#125;
        Solve();
        for (int i = n + 1; i &amp;lt;= 2 * n; ++i)
            res += g[mat[i]][i];
        print(res, &amp;#39;\n&amp;#39;);
