                u[x] + u[i] - g[x][i]);
        &amp;#125;
    &amp;#125;
    return 0;
&amp;#125;
void Solve(void) &amp;#123;
    for (int i = 1; i &amp;lt;= 2 * n; ++i)
        mat[i] = u[i] = 0;
    for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
        u[i] = -inf;
        for (int j = n + 1; j &amp;lt;= 2 * n; ++j)
            u[i] = max(u[i], g[i][j]);
    &amp;#125;
    for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
        for (;;) &amp;#123;
            ++now;
            for (int j = n + 1; j &amp;lt;= 2 * n; ++j)
                up[j] = inf;
            if (Find(i))
                break;
