            &amp;#125;
            for (int j = 1; j &amp;lt;= n; ++j) &amp;#123;
                if (vis[j] == now)
                    u[j] -= Delta;
            &amp;#125;
            for (int j = n + 1;
                            j &amp;lt;= 2 * n; ++j) &amp;#123;
                if (vis[j] == now)
                    u[j] += Delta;
            &amp;#125;
        &amp;#125;
    &amp;#125;
    return;
&amp;#125;
int main() &amp;#123;
    while (read(n)) &amp;#123;
        read(m);
        res = 0;
        for (int i = 1; i &amp;lt;= 2 * n; ++i) &amp;#123;
            for (int j = 1; j &amp;lt;= 2 * n; ++j) 
                g[i][j] = -inf;
        &amp;#125;
        while (m--) &amp;#123;
            read(x), read(y), read(w);
            g[x][y + n] = max(g[x][y + n], -w);
