        for (int i = n + 1; i &amp;lt;= 2 * n; ++i) &amp;#123;
            if (g[mat[i]][i] &amp;lt;= -inf) &amp;#123;
                puts(&amp;quot;-1&amp;quot;);
                goto NoSol;
