    &amp;#125;
    for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
        for (;;) &amp;#123;
            ++now;
            for (int j = n + 1; j &amp;lt;= 2 * n; ++j)
                up[j] = inf;
            if (Find(i))
                break;
