                    u[j] += Delta;
            &amp;#125;
        &amp;#125;
    &amp;#125;
    return;
&amp;#125;
int main() &amp;#123;
    read(T);
    while(T--) &amp;#123;
        read(n), read(m);
        res = 0;
        for (int i = 1; i &amp;lt;= 2 * n; ++i) &amp;#123;
            for (int j = 1; j &amp;lt;= 2 * n; ++j) 
                g[i][j] = -inf;
        &amp;#125;
