    int p = std::max_element(dep.begin() + 1, dep.end()) - dep.begin();
    bool flag = 0;
    std::vector&amp;lt;int&amp;gt; node, cnt(n + 1), siz(n + 1);
    DFS = [&amp;amp;](int x, int fa) &amp;#123;
        siz[x] = 1;
        for (auto i : g[x])
            if (i != fa) &amp;#123;
                DFS(i, x);
                siz[x] += siz[i], ++cnt[x];
            &amp;#125;
        if (siz[x] &amp;gt; 1) &amp;#123;
