        for (; cnt[i]--; std::cout &amp;lt;&amp;lt; ++now &amp;lt;&amp;lt; &amp;#39; &amp;#39;);
        std::cout &amp;lt;&amp;lt; rt &amp;lt;&amp;lt; &amp;#39; &amp;#39;;
    &amp;#125;
