        &amp;#125;;
        DFS(p, -1);
    &amp;#125;
    else &amp;#123;
        std::function&amp;lt;bool(int, int)&amp;gt; DFS = [&amp;amp;](int x, int fa) &amp;#123;
            if (x == q)
                return true;
            bool ret = false;
            for (auto i : g[x])
                if (i != fa)
                    ret |= DFS(i, x);
            if (ret &amp;amp;&amp;amp; dep[x] == dep[q] / 2 + 1)
                edge.emplace_back(x, fa);
            return ret;
        &amp;#125;;
        DFS(p, -1);
    &amp;#125;
    long long res = inf;
    for (auto [x, y] : edge) &amp;#123;
