            int son = 0;
            for (auto i : g[x])
                if (i != fa)
                    ++son, DFS(i, x, dep + 1);
            cnt[dep] = std::max(cnt[dep], son);
            return;
        &amp;#125;;
        DFS(x, y, 1), DFS(y, x, 1);
        auto now(2ll);
        for (int i = 1; i &amp;lt;= n &amp;amp;&amp;amp; cnt[i]; ++i)
            now *= cnt[i];
        res = std::min(res, now);
    &amp;#125;
    for (auto rt : node) &amp;#123;
