            g[fa].push_back(i);
            if (i == 4 &amp;amp;&amp;amp; *std::max_element(deg.begin() + 1, deg.begin() + 5) == 3)
                std::cout &amp;lt;&amp;lt; 2 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
            else
                std::cout &amp;lt;&amp;lt; i % 2 &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
