                    &amp;#125;
                    else &amp;#123;
                        int p6 = g[p5][0], p7 = g[p5][1];
                        col[p6] = col[p7] = 1, now = 1;
                    &amp;#125;
                &amp;#125;
            &amp;#125;
            else &amp;#123;
                int p2 = g[1][0], p3 = g[1][1];
                if (siz[p2] &amp;gt; siz[p3])
                    std::swap(p2, p3);
                if (siz[p2] == 2 &amp;amp;&amp;amp; siz[p3] == 2)
                    col[p2] = col[p3] = 1, now = -1;
                else if (siz[p2] == 2 &amp;amp;&amp;amp; siz[p3] == 3) &amp;#123;
