        std::cout &amp;lt;&amp;lt; (int)res2.size() &amp;lt;&amp;lt; &amp;#39;\n&amp;#39;;
        for (auto i : res2)
            std::cout &amp;lt;&amp;lt; i &amp;lt;&amp;lt; &amp;#39; &amp;#39;;
