            if (w)
                merge(x, y), merge(x + n, y + n);
            else
                merge(x, y + n), merge(x + n, y);
        for (int i = 1; i &amp;lt;= n; ++i) &amp;#123;
            ++siz[find(i)];
            if (find(i) == find(n + i))
                return false;
