        std::cin &amp;gt;&amp;gt; c[i];
    std::sort(c.begin() + 1, c.end(), std::greater&amp;lt;int&amp;gt; ());
    std::partial_sum(c.rbegin(), c.rend(), c.rbegin());
    using arr = std::vector&amp;lt;int&amp;gt;;
