    auto upd = [&amp;amp;](int i, int &amp;amp;t, int &amp;amp;x, int y) &amp;#123;
        if (t == i)
            x = std::min(x, y);
        else
            x = y, t = i;
        return;
    &amp;#125;;
    for (int j = 0, k = 0; j &amp;lt; n; ++j, k ^= 1) &amp;#123;
        for (int b = 0; b &amp;lt;= n; ++b)
            for (int a = 0; a &amp;lt;= n; ++a)
